function formatTelNumber(input) {
        // Trim the remaining input to ten characters, to preserve phone number format
        input = input.substring(0, 10);
        // Based upon the length of the string, we add formatting as necessary
        var size = input.length;
        if (size == 0) {
                input = input;
        } else if (size < 3) {
                input = '(' + input;
        } else if (size < 7) {
                input = '(' + input.substring(0, 3) + ') ' + input.substring(3, 6);
        } else {
                input = '(' + input.substring(0, 3) + ') ' + input.substring(3, 6) + '-' + input.substring(6, 10);
        }
        return input;
}
contact = this.contact;
if (this.contact && this.contact.cellPhone) {
        cellPhone = this.contact.cellPhone.replace(/\D/g, '');
        contact.cellPhone = formatTelNumber(cellPhone);
}
if (this.contact && this.contact.workPhone) {
        workPhone = this.contact.workPhone.replace(/\D/g, '');
        contact.workPhone = formatTelNumber(workPhone);
}
return contact;

