let isMinor = this.isMinor;

function getAge(dateString) {
    var today = new Date();
    var birthDate = new Date(dateString);
    var age = today.getFullYear() - birthDate.getFullYear();
    var m = today.getMonth() - birthDate.getMonth();
    if (m < 0 || (m === 0 && today.getDate() < birthDate.getDate())) {
        age--;
    }
    return age;
}
if (this.dateOfBirth) {
    isMinor = getAge(this.dateOfBirth) < 18;
}

return isMinor;

var today = new Date();
var dd = today.getDate();
var mm = today.getMonth()+1; 
var yyyy = today.getFullYear();
if(dd<10) 
{
    dd='0'+dd;
} 

if(mm<10) 
{
    mm='0'+mm;
} 
today = yyyy+'-'+mm+'-'+dd;
return today;

let requiredId = "None";

if (this.enableRequired != undefined && this.isUSPerson != undefined && this.enableRequired == true) {
    if (this.isUSPerson == true) {
        requiredId = "US Document";
    } else {
        requiredId = "Other Document";
    }
}

return requiredId;

let idType = "";

if (this.isUSPerson != undefined && this.isUSPerson == true) {
    idType = this.idTypeUS.pmName;
} else {
    idType = this.idTypeOther.pmName;
}

return idType;

let idIssueLocation = "";

if (this.isUSPerson != undefined && this.isUSPerson == true) {
    idIssueLocation = this.idIssueLocationUS;
} else {
    idIssueLocation = this.idIssueLocationOther;
}

return idIssueLocation;

let idTypeCore = "";

if (this.isUSPerson != undefined && this.isUSPerson == true) {
    idTypeCore = this.idTypeUS.coreCode;
} else {
    idTypeCore = this.idTypeOther.coreCode;
}

return idTypeCore;

let steps = {
    "one": "People",
    "two": "Products",
    "three": "Disclosures",
    "total": 3
};
if (this.trustAccount == "Yes" || this.addOtherPeople == "Yes") {
    steps = {
        "one": "People",
        "two": "Trust",
        "three": "Products",
        "four": "Disclosures",
        "total": 4
    };
}

return steps;

let pageValidation = {
    isValid: true,
    validationMessage: "",
    personalInfoCompleted: true,
    contactInfoCompleted: true,
    addressInfoCompleted: true,
    employmentInfoCompleted: true,
    citizenInfoCompleted: true,
    identificationInfoCompleted: true
};

let sendingToClient = (this.bankerChoice === "Complete");

function valueIsValid(value) {
    return (value !== null && value !== "" && (typeof value !== "undefined"));
}

if (Array.isArray(this.people) && this.people.length > 0) {
    let i = 1;
    this.people.forEach(person => {
        // Personal Information
        if (!valueIsValid(person.firstName)) {
            pageValidation.personalInfoCompleted = false;
            pageValidation.validationMessage += "* Please provide person " + i + " first name <br>";
        }
        if (!valueIsValid(person.lastName)) {
            pageValidation.personalInfoCompleted = false;
            pageValidation.validationMessage += "* Please provide person " + i + " last name <br>";
        }
        // Contact Information
        if (!valueIsValid(person.email)) {
            pageValidation.contactInfoCompleted = false;
            pageValidation.validationMessage += "* Please provide person " + i + " email address <br>";
        }
        if (!valueIsValid(person.cellPhone)) {
            pageValidation.contactInfoCompleted = false;
            pageValidation.validationMessage += "* Please provide person " + i + " cell phone <br>";
        }
        if (!sendingToClient && (person.isPresent || person.isPrimaryContact)) {
            // Personal Information
            if (!valueIsValid(person.SSN)) {
                pageValidation.addressInfoCompleted = false;
                pageValidation.validationMessage += "* Please provide person " + i + " SSN <br>";
            }

			if (person.isMinor == false) {
				// Address Information
				if (!valueIsValid(person.address)) {
					pageValidation.addressInfoCompleted = false;
					pageValidation.validationMessage += "* Please provide person " + i + " street address <br>";
				}
				if (!valueIsValid(person.city)) {
					pageValidation.addressInfoCompleted = false;
					pageValidation.validationMessage += "* Please provide person " + i + " city <br>";
				}
				// Employment Information
				if (!valueIsValid(person.employmentStatus)) {
					pageValidation.employmentInfoCompleted = false;
					pageValidation.validationMessage += "* Please provide person " + i + " employment status <br>";
				}
				if (!valueIsValid(person.occupation)) {
					pageValidation.employmentInfoCompleted = false;
					pageValidation.validationMessage += "* Please provide person " + i + " occupation <br>";
				}

				// Citizen Information
				if (!valueIsValid(person.isUSCitizen)) {
					pageValidation.citizenInfoCompleted = false;
					pageValidation.validationMessage += "* Please indicate if person " + i + " is a US citizen <br>";
				}
				if (!valueIsValid(person.foreignPolitical)) {
					pageValidation.citizenInfoCompleted = false;
					pageValidation.validationMessage += "* Please provide person " + i + " foreign political relationship <br>";
				}

				// Identification Information
				if (!valueIsValid(person.idNumber)) {
					pageValidation.identificationInfoCompleted = false;
					pageValidation.validationMessage += "* Please provide person " + i + " ID number <br>";
				}
				if (!valueIsValid(person.idType)) {
					pageValidation.identificationInfoCompleted = false;
					pageValidation.validationMessage += "* Please provide person " + i + " ID type <br>";
				}
			}
        }

        i++;
    });
}


pageValidation.isValid = pageValidation.personalInfoCompleted && pageValidation.contactInfoCompleted
    && pageValidation.addressInfoCompleted && pageValidation.citizenInfoCompleted
    && pageValidation.employmentInfoCompleted && pageValidation.identificationInfoCompleted

return pageValidation;

let officersInvolved;
if (Array.isArray(this.officersInvolved)) {
    officersInvolved = this.officersInvolved;
} else {
    officersInvolved = [];
}
let officer = {};
let taskName = "Banker Application";

if (typeof this._user !== "undefined") {
    officer.task = taskName;
    officer.id = this._user.id;
    officer.name = this._user.fullname;
    officer.email = this._user.email;

    let today = new Date();
    
    officer.dateTime = today.toISOString();

    if (this.currentRole == "Customer") {
        officer.role = "Customer";
    }
    else if (this.currentRole == "Banker") {
        officer.role = "Banker";
    }
    // check if the task exists, if existing, update it else insert
    let i = officersInvolved.findIndex((officerInvolved) => officerInvolved.task === taskName);
    if (i < 0) {
        officersInvolved.push(officer);
    } else {
        officersInvolved[i] = officer;
    }
}

return officersInvolved;

