if(empty(this.processIdFromUrl)) { 
    var url = window.location.href;
    var arr = url.split("/");
    return arr[4];
} else {
    return this.processIdFromUrl
}

function empty(val) {
    return [null, "", undefined].indexOf(val) != -1;
}

if(this.receiptUpload != null) {
    var button = $("[selector='btn-new'] button");
    setTimeout(function() {
        button.trigger('click');
    }, 2500);
}

return true;

function compareData(exp, receiptMerchant, receiptDate, receiptAmount, reportSelected, receiptCategory, receiptComments ) {
    if (exp.requestStatus == "Scanning") { return false; }
    if (exp.receiptMerchant !== receiptMerchant ||
        exp.receiptDate !== receiptDate ||
        exp.reportSelected !== reportSelected || 
        parseFloat(exp.receiptAmount) !== parseFloat(receiptAmount) ||
        exp.category !== receiptCategory ||
        (exp.comments || "") !== receiptComments) {
        return true;
    }
    return false;
}

if(this.selectedExpense != null && this.selectedExpense != undefined && this.selectedExpense != ""){
    return compareData(this.selectedExpense, this.receiptMerchant, this.receiptDate, this.receiptAmount, this.reportSelected, this.receiptCategory, this.receiptComments);
}
return false;

if([null, "", undefined].indexOf(this.reportedExpensesArr) != -1) { return true; }
if([null, "", undefined].indexOf(this.reportedExpensesArr.receipts) != -1) { return true; }

var rep = this.reportedExpensesArr.receipts;
let flag = false;

for (const element of rep) {
    if([null, "", undefined].indexOf(element.data.receiptAmount) != -1) {
        flag = true;
        break;
    }
};

return flag;

let button=this.action
document.querySelectorAll("[selector='clean'] textarea").forEach(function(element){
    element.value = ""
})

