computed.formula:
if([null, "", undefined].indexOf(this.expenses) != -1) { return null; }
if([null, "", undefined].indexOf(this.expenses.receipts) != -1) { return null; }

var exp = this.expenses.receipts;
var ret = null;

for(var i = 0; i < exp.length; i++) {
	if(exp[i].receiptButton != null) {
		exp[i]["data"]["expenseId"] = exp[i].receiptButton;
		exp[i].receiptButton = null;
		ret = exp[i]["data"];
	}
}

return ret;

if([null, "", undefined].indexOf(this.selectedExpense) != -1) {
    return null;
}

return this.selectedExpense.expenseId;

var hasScan = this.expenses.receipts.some(function(item) {
    return item.data.requestStatus === "Scanning";
});
if(hasScan){
    var button = $("div[selector='buttonScan']").find("button[name='checkExpensesScan']");
    setTimeout(function() {
        button.trigger('click');
        //button.prop('disabled', true);
    }, 5000);
}
return hasScan;

