computed.formula:
var receipts = this.unreportedExpenses.receipts;
var selects = receipts.filter(r => r.expenseToSend).length;
return selects;

var dataArray = Array.isArray(this.unreportedDataArray) && this.unreportedDataArray.length === 0 ? [] : this.unreportedDataArray != undefined ? this.unreportedDataArray : [];
function addToArray(newObject) {
    var existingIndex = dataArray.findIndex(function(item) {
        return item.id === newObject.id;
    });
    if (existingIndex !== -1) {
        dataArray[existingIndex] = newObject;
    } else {
        dataArray.push(newObject);
    }
}
this.unreportedExpenses.receipts.forEach((unr)=>{
    addToArray(unr);
});

return dataArray;

