let reportSelectedCopy = this.reportSelectedCopy;
if(this.action != "expense_update" && this.selectedExpense != null && this.selectedExpense != "" && this.selectedExpense != undefined){
    reportSelectedCopy = this.reportSelected;
}

const reportsDataCopy = {
    reportSelected: reportSelectedCopy
};
return reportsDataCopy;

