//select list with data
let dataSelected = this.form_select_list_1;
//loop we want to fill
let dataLoop = this.loop_1 || [];

let newRecordSelected = dataSelected.filter(item => {
    return !dataLoop.find(out => out.value == item.value)
});
// add a copy of the new selected item
newRecordSelected.forEach(item => dataLoop.push(JSON.parse(JSON.stringify(item))));

let removeFromLoop = dataLoop.filter(item => !dataSelected.find(inp => inp.value == item.value) );
// remove From Loop the items
removeFromLoop.forEach(item => {
    let index = dataLoop.indexOf(item)
    dataLoop.splice(index, 1)
});

