screens.custom_css:
[selector='page-nav'] button {
    width: 100%;
}

[selector='tab-nav'] button.btn {
    width: 100%;
    text-transform: none;
    padding: 0;
}
[selector='tab-nav'] .form-group {
    margin-bottom: 0px;
}
[selector='tab-nav'] {
    border-bottom: 2px solid #205072;
    margin-bottom: 40px;
}
[selector='tab-nav'] .nav-tab {
    padding: 7px 12px;
    background: #205072;
    border-radius: 5px 5px 0px 0px;
    color: #fff;
    text-align: center;
    margin: 0px;
}
