var accounts = [];
if (this.accounts && Array.isArray(this.accounts)) {
    accounts = [...this.accounts];
}

if (this.additionalAccounts && this.additionalAccounts.length > 0) {
    for (let i = 0; i < this.additionalAccounts.length; i++) {
        if (this.additionalAccounts[i].productType && this.accounts[i]) {
            accounts[i].product = this.additionalAccounts[i].productType;
        }
        else if (this.additionalAccounts[i].productType) {
            accounts.push({ product: this.additionalAccounts[i].productType });
        }
    }
}
if (this.fundingMethod && this.fundingMethod != null) {
    for (let i = 0; i < this.accounts.length; i++) {
         accounts[i].requireInitialDepositOther = "Yes";     

    }
}
return accounts;

if (this.fundingMethod != null && this.fundingMethod != ""){ // No val
    return true;
}
else{
    return false;
}

screens.custom_css:
label, input, p, select, li {
    font-size: 90%;
}
[selector='page-nav'] button {
    width: 100%;
}

[selector='no-label'] label {
    display: none;
}
/*
[selector='no-label'] .multiselect__single, 
[selector='no-label'] .multiselect__element {
    text-align: center;
}
*/

[selector='text-center'] {
    text-align: center;
}


span.step {
    width: 30px;
    height: 30px;
    display: inline-block;
    padding: 3px 10px;
    border-radius: 50%;
}

button.btn.btn-link {
    text-transform: none;
}

.row.justify-content-md-center > .col-md-auto {
    width: 100%;
}
.row.justify-content-md-center > .col-md-auto button {
    width: auto;
}
.row.justify-content-md-center > .col-md-auto .fas::before {
    text-transform: capitalize;
    font-family: "Open Sans";
    font-weight: normal;
}

[selector='joint-owner'] .row.justify-content-md-center > .col-md-auto .fas.fa-plus::before {
    content: "Add Joint Owner";
}
[selector='joint-owner'] .row.justify-content-md-center > .col-md-auto .fas.fa-minus::before {
    content: "Remove Joint Owner";
}
[selector='signer'] .row.justify-content-md-center > .col-md-auto .fas.fa-plus::before {
    content: "Add Signer";
}
[selector='signer'] .row.justify-content-md-center > .col-md-auto .fas.fa-minus::before {
    content: "Remove Signer";
}
[selector='additional-accounts'] .row.justify-content-md-center > .col-md-auto .fas.fa-plus::before {
    content: "Add Another Account";
}
[selector='additional-accounts'] .row.justify-content-md-center > .col-md-auto .fas.fa-minus::before {
    content: "Remove Account";
}
/* Steps Styles*/
[selector='steps']  > div > .row > div {
    padding: 0px;
}

[selector='steps'] .row > div:first-child .step-icon-wrap::before {
    display: none
}

[selector='steps'] .row > div:last-child .step-icon-wrap::after {
    display: none
}
[selector='steps'] .step-icon-wrap {
    display: block;
    position: relative;
    width: 100%;
    height: 60px;
    text-align: center
}

[selector='steps'] .step-icon, .step .step-icon {
    display: inline-block;
    position: relative;
    width: 60px;
    height: 60px;
    border-radius: 50%;
    font-size: 30px;
    line-height: 59px;
    z-index: 5
}

[selector='steps'] .step-icon em, .step .step-icon em {
    margin-left: 7px;
}
[selector='steps'] .step-icon-wrap::before {
    left: 0
}

[selector='steps'] .step-icon-wrap::after {
    right: 0
}
[selector='steps'] .step-icon-wrap::before,
[selector='steps'] .step-icon-wrap::after {
    display: block;
    position: absolute;
    top: 50%;
    width: 50%;
    height: 3px;
    margin-top: -1px;
    background-color: #e1e7ec;
    content: '';
    z-index: 1
}

[selector='required'] label::after {
  content: " *";
  color: red;
}

 [selector='checkbox'] .form-check{
    display: inline;
    padding-right: 5px;
    margin-right: 10px;
 }

 [selector='hidden'] {
     visibility: hidden;
 }

 [selector='option-buttons'] button {
     text-transform: none;
     text-align: center;
     border: 1px solid #ececec;
     width: 100%;
 }
